import com.company.gps.Location;

public class SecurityCrewMember extends CrewMember {
	private Location[] authoritySegment = new Location[4];

	public SecurityCrewMember(String memberName, String memberPhone, String memberEmail, Location[] authoritySegment) {
		super(memberName, memberPhone, memberEmail);

		this.authoritySegment = authoritySegment;
	}

	@Override
	public void notify(String message) {
		System.out.println("SECURITY NOTIFIED " + message);
	}
	
	public boolean isInSegment(Location location) {
		return location.getLatitude() >= authoritySegment[0].getLatitude() && location.getLongitude() >= authoritySegment[0].getLongitude()
				&& location.getLatitude() >= authoritySegment[1].getLatitude() && location.getLongitude() <= authoritySegment[1].getLongitude()
				&& location.getLatitude() <= authoritySegment[2].getLatitude() && location.getLongitude() <= authoritySegment[2].getLongitude()
				&& location.getLatitude() <= authoritySegment[3].getLatitude() && location.getLongitude() >= authoritySegment[3].getLongitude();
	}
}
